import styled from 'styled-components';

import favorite from '../../assets/imgs/badges/icons8-favorite-100.png';
import ingredients from '../../assets/imgs/badges/icons8-ingredients-100.png';
import search from '../../assets/imgs/badges/icons8-search-100.png';

export const BadgeContainerStyled = styled.div`
	height: 100%;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	width: 30vh;
	margin: 0 1rem;
	text-align: center;
	width: 80%;

	img {
		width: 5rem;
		height: 5rem;
	}

	h2 {
		font-size: 0.9rem;
		margin-bottom: 0.5rem;
	}

	p {
		font-size: 1.2rem;
		line-height: 1.6rem;
	}
`;

function BadgeContainer() {
	const badges = [
		{
			id: 1,
			image: ingredients,
			heading: <h2> Ingredients </h2>,
			text: (
				<p>
					{' '}
					Simply Input your Ingredients into the Field Below and Get Started Looking up your
					Recipes!
				</p>
			),
		},

		{
			id: 2,
			image: favorite,
			heading: <h2> Favorites </h2>,
			text: <p> Save Your Favorite Meals, Click the Heart Icon Beside Each Recipe!</p>,
		},

		{
			id: 3,
			image: search,
			heading: <h2> Search </h2>,
			text: (
				<p> Search for any Recipe, specify by Allergens, or by any ingredient, we have it all! </p>
			),
		},
	];

	return (
		<BadgeContainerStyled>
			{badges.map(badge => (
				<div key={badge.id}>
					<Badge image={badge.image} heading={badge.heading} text={badge.text} />
				</div>
			))}
		</BadgeContainerStyled>
	);
}

export default BadgeContainer;
