import styled from 'styled-components';

export const BadgeContainer = styled.div`
	height: 100%;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	width: 30vh;
	margin: 0 1rem;
	text-align: center;
	width: 80%;

	img {
		width: 5rem;
		height: 5rem;
	}

	h2 {
		font-size: 0.9rem;
		margin-bottom: 0.5rem;
	}

	p {
		font-size: 1.2rem;
		line-height: 1.6rem;
	}
`;
