import React from 'react';

const Icon = styled.img`
	width: 5rem;
	height: 5rem;
`;

function Badge({ image, heading, text }) {
	return (
		<div>
			<img src={image} alt='' />
			<h2> {heading} </h2>
			<p> {text} </p>
		</div>
	);
}

export default Badge;
