import styled from 'styled-components';

const TopContainer = styled.div`
	margin: auto;
	text-align: center;
	width: 60%;

	h1 {
		margin-bottom: 1rem;
		font-size: 2.5rem;
	}
	p {
		font-size: 1.4em;
		line-height: 2rem;
	}
`;

export default TopContainer;
