import styled from 'styled-components';

const Icon = styled.img`
	width: 5rem;
	height: 5rem;
`;

const Heading = styled.h2`
	font-size: 0.9rem;
	margin-bottom: 0.5rem;
`;

const Text = styled.p`
	text-align: center;
	width: 80%;
`;

function Badge({ image, heading, text }) {
	return (
		<div>
			<Icon src={image} alt='' />
			<Heading> {heading} </Heading>
			<Text> {text} </Text>
		</div>
	);
}

export default Badge;
