import React from 'react';

import styled from 'styled-components';
import GreekMeatballs from '../../assets/imgs/GreekMeatballs.jpeg';

import Card from '../Card/Card';

const RandomRecipe = styled.div`
	display: flex;
	justify-content: center;
	margin: 2rem;
`;
const axios = require('axios');

const options = {
	method: 'GET',
	url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/random',
	params: { tags: 'vegetarian', number: '1' },
	headers: {
		'X-RapidAPI-Key': '2ad55f65fbmshcfe0e39bda9c68bp12a597jsn89ff591ad860',
		'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
	},
};

axios
	.request(options)
	.then(response => {
		console.log(response.data);
		// setRecipe(response.data);
	})
	.catch(function (error) {
		console.error(error);
	});

function RandomRecipes() {
	const recipe = {
		title: 'Greek Meetballs',
		image: GreekMeatballs,
		content: `These juicy Greek Meatballs rock! A traditional recipe, beautifully flavoured with red
		onion, fresh parsley, a touch of mint and hint of dried oregano. These meatballs are
		slightly crisp on the outside and are brilliant served as an appetiser with tzatiki and
		pita bread as part of a mezze spread (very Greek!), or as a meal with a salad.`,
	};

	return (
		<RandomRecipe className='card-containers'>
			<Card title={recipe.title} img={recipe.image} content={recipe.content} />
		</RandomRecipe>
	);
}

export default RandomRecipes;
