import { useEffect, useState } from 'react';
import styled from 'styled-components';
import Card from '../Card/Card';

const Random = () => {
	const [random, setRandom] = useState([]);

	const getRandomRecipes = async () => {
		const getData = localStorage.getItem('popular');

		if (getData) {
			setRandom(JSON.parse(getData));
		} else {
			const options = {
				method: 'GET',
				headers: {
					'X-RapidAPI-Key': '9d9cfe1897msh741bd79fb799e65p10e075jsn75713fa5c441',
					'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
				},
			};
			fetch(
				'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/random?tags=vegetarian%2Cdessert&number=1',
				options
			)
				.then(response => response.json())
				.then(response => console.log(response))
				.catch(err => console.error(err));

			const resp = await fetch(
				`https://api.spoonacular.com/recipes/random?apiKey=${process.env.REACT_APP_FOOD_API_KEY}&number=3`
			);

			const data = await resp.json();
			setRandom(data.recipes);
			localStorage.setItem('popular', JSON.stringify(data.recipes));
			console.log(data.recipes);
		}
	};

	useEffect(() => {
		getRandomRecipes();
	}, []);

	return (
		<RandomRecipe className='card-containers'>
			{random.map(({ title, id, image, summary }) => (
				<Card key={id} title={title} img={image} content={summary} />
			))}
		</RandomRecipe>
	);
};

const RandomRecipe = styled.div`
	display: flex;
	justify-content: center;
	margin: 2rem;
`;

export default Random;
