import logo from '../../assets/imgs/bold.png';

import './Header.css';
import Modal from '../Modal/Modal';
import { useState } from 'react';

function Header({ children, ...props }) {
	const [isOpen, setIsOpen] = useState(false);
	const [showLogin, setShowLogin] = useState(false);
	const [showSignup, setShowSignup] = useState(false);

	// const renderLogin = () => {
	// 	setLogin(!login);
	// };

	// const renderSignup = () => {
	// 	setLogin(prev => !prev);
	// };

	const closeModal = () => {
		setIsOpen(prev => !prev);
	};

	const openModal = () => {
		if (showLogin || showSignup) {
			setIsOpen(true);
		}
	};

	function toggleModal() {
		setIsOpen(prev => !prev);
	}

	return (
		<div className='Header'>
			<div className='header-brand'>
				<img className='header-logo' alt='logo' src={logo} />

				<h1 className='header-h1'> BiaNova </h1>
			</div>

			<div className='login'>
				<p>
					<button onClick={setShowLogin(prev => !prev)} className='header-btn'>
						{' '}
						Log In{' '}
					</button>{' '}
					|{' '}
					<button onClick={setShowSignup(prev => !prev)} className='header-btn'>
						{' '}
						Sign Up{' '}
					</button>
				</p>
			</div>
			<Modal open={isOpen} toggleModal={() => toggleModal()}></Modal>
		</div>
	);
}

export default Header;
